import UIKit

class CostViewController: UITableViewController {
    // MARK: - Properties
	var presenter: CostPresenterProtocol!
    var costArray: [Cost]!

    // MARK: - App life cycle
	override func viewDidLoad() {
        super.viewDidLoad()
        self.presenter.showCost()
        self.setupTableView()
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        setupNavBar()
    }
    
    // MARK: - TableView
    func setupTableView() {
        tableView = UITableView(frame: CGRect(x: 0, y: 0, width: self.view.frame.width, height: self.view.frame.height))
        tableView.dataSource = self
        tableView.delegate = self
        tableView.register(CostCell.self, forCellReuseIdentifier: CostCell.reuseId)
        tableView.rowHeight = 120
        tableView.separatorStyle = .none
    }
    
    // MARK: - Setup NavigationBar
    func setupNavBar() {
        self.navigationController?.navigationBar.prefersLargeTitles = false
    }
}

// MARK: - Binding
extension CostViewController: CostViewProtocol {
    func setCost(cost: [Cost]) {
        costArray = cost
    }
    
    func configureAddButton(addButton: UIBarButtonItem) {
        self.navigationItem.rightBarButtonItem = addButton
    }
    
    func present(viewControllerToPresent: UIViewController) {
        self.present(viewControllerToPresent, animated: true)
    }
}
